(6, 'wc-admin-draw-attention', 'info', 'en_US', 'How to draw attention to your online store', 'To get you started, here are seven ways to boost your sales and avoid getting drowned out by similar, mass-produced products competing for the same buyers.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-03 13:12:35', NULL, 0, 'plain', '', 0, 'info'),
(7, 'ecomm-need-help-setting-up-your-store', 'info', 'en_US', 'Need help setting up your Store?', 'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. Were happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.', '{}', 'pending', 'woocommerce.com', '2020-10-03 13:16:15', NULL, 0, 'plain', '', 0, 'info'),
(8, 'woocommerce-services', 'info', 'en_US', 'WooCommerce Services', 'WooCommerce Services helps get your store ready to sell as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.', '{}', 'unactioned', 'woocommerce.com', '2020-10-03 13:16:16', NULL, 0, 'plain', '', 0, 'info'),
(9, 'ecomm-unique-shopping-experience', 'info', 'en_US', 'For a shopping experience as unique as your customers', 'Product Add-Ons allow your customers to personalize products while theyre shopping on your online store. No more follow-up email requestscustomers get what they want, before theyre done checking out. Learn more about this extension that comes included in your plan.', '{}', 'pending', 'woocommerce.com', '2020-10-03 13:16:16', NULL, 0, 'plain', '', 0, 'info'),
(10, 'mailchimp-for-woocommerce-incomplete-install', 'warning', 'en_US', 'Mailchimp For WooCommerce', 'Plugin is not yet connected to a Mailchimp account. To complete the connection, open the settings page.', '{\"getting_started\":true,\"activated\":1601730985,\"activated_formatted\":\"October 3rd\"}', 'unactioned', 'mailchimp-for-woocommerce', '2020-10-03 13:16:25', NULL, 0, 'plain', '', 0, 'info'),
(11, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics  wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-05 13:57:46', NULL, 0, 'plain', '', 0, 'info'),
(12, 'wc-admin-learn-more-about-product-settings', 'info', 'en_US', 'Learn more about Product Settings', 'In this video you\'ll find information about configuring product settings, such as how they are displayed, editing inventory options and so on.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-05 13:57:48', NULL, 0, 'plain', '', 0, 'info'),
(13, 'wc-admin-online-clothing-store', 'info', 'en_US', 'Start your online clothing store', 'Starting a fashion website is exciting but it may seem overwhelming as well. In this article, we\'ll walk you through the setup process, teach you to create successful product listings, and show you how to market to your ideal audience.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-05 13:57:48', NULL, 0, 'plain', '', 0, 'info'),
(14, 'wc-admin-orders-milestone', 'info', 'en_US', 'First order received', 'Congratulations on getting your first order! Now is a great time to learn how to manage your orders.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-06 08:58:32', NULL, 0, 'plain', '', 0, 'info'),
(15, 'wc-admin-launch-checklist', 'info', 'en_US', 'Ready to launch your store?', 'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.', '{}', 'unactioned', 'woocommerce-admin', '2020-10-07 16:51:49', NULL, 0, 'plain', '', 0, 'info');
